var oop = require('oop');

var Mode = require('mode').Mode;



var HTML = oop.Class({
	name: 'HTML',
	parent: Mode,

	constructor: function() {
		Mode.call(this, {
			parser: require('./parser'),
			stylesheet: require('./stylesheet'),
			outline: require('./outline'),

			highlightedNodes: [
				// Explicit
				'opening',
				'closing',
				'inline',
				'text',
				'comment',
				'cdata',
				'directive',

				// Implicit
				'eol',
				'tabs',
				'spaces'
			]
		});
	}
});



module.exports = new HTML();
