var Mode = require('mode').Mode;



var HTML = new Mode({
	parser: require('./parser'),
	stylesheet: require('./stylesheet'),
	outline: require('./outline'),

	highlightedNodes: [
		// Explicit
		'opening',
		'closing',
		'inline',
		'text',
		'comment',
		'cdata',
		'directive',

		// Implicit
		'eol',
		'tabs',
		'spaces'
	]
});

HTML.displayName = 'HTML';



module.exports = HTML;
