var oop = require('oop');



var Font = oop.Class({
	name: 'Font',

	constructor: function(input) {
		oop.processProperties(this, input, {
			inputToSpec: {
				'String': 'family',
				'Number': 'height',
				'Boolean': 'bold'
			},
			properties: [
				{names: ['family', 'f']},
				{names: ['height', 'h']},
				{names: ['bold', 'b']},
				{names: ['italic', 'i']},
				{names: ['strike', 's']},
				{names: ['underline', 'u']}
			]
		});
	},

	factories: {
		standard: {
			values: {
				family: 'Arial',
				height: '12',
				bold: false,
				italic: false,
				strike: false,
				underline: false
			}
		},
		family: {
			args: {family: 0},
			values: {
				height: '12',
				bold: false,
				italic: false,
				strike: false,
				underline: false
			}
		},

	}
});



exports.Font = Font;
