var oop = require('oop');



var Font = oop.Class({
	name: 'Font',

	desc: 'Represents font data.',

	schema: {
		inputToSpec: {
			'String': 'family',
			'Number': 'height',
			'Boolean': 'bold'
		},
		properties: [
			{names: ['family', 'f'], type: oop.types.String},
			{names: ['height', 'h'], type: oop.types.Number},
			{names: ['bold', 'b'], type: oop.types.Boolean},
			{names: ['italic', 'i'], type: oop.types.Boolean},
			{names: ['strike', 's'], type: oop.types.Boolean},
			{names: ['underline', 'u'], type: oop.types.Boolean}
		]
	},

	factories: {
		standard: {
			values: {
				family: 'Arial',
				height: '12',
				bold: false,
				italic: false,
				strike: false,
				underline: false
			}
		},
		family: {
			args: {family: 0},
			values: {
				height: '12',
				bold: false,
				italic: false,
				strike: false,
				underline: false
			}
		},

	}
});



exports.Font = Font;
