var oop = require('oop');

var Color = require('./color').Color;
var Font = require('./font').Font;



var Style = oop.Class({
	name: 'Style',

	constructor: function(input) {
		oop.processProperties(this, input, {
			inputToSpec: {
				'String': 'color',
				'Number': 'color'
			},
			properties: [
				{names: ['color', 'c'], ctor: Color, mixed: true},
				{names: ['background', 'bg', 'backgroundColor'], ctor: Color},
				{names: ['font', 'f'], ctor: Font, mixed: true}
			]
		});
	}
});



exports.Style = Style;
