var oop = require('oop');

var Color = require('./color').Color;
var Font = require('./font').Font;



var Style = oop.Class({
	name: 'Style',

	schema: {
		inputToSpec: {
			'String': 'color',
			'Number': 'color'
		},
		properties: [
			{names: ['color', 'c'], type: Color, mixed: true},
			{names: ['background', 'bg', 'backgroundColor', 'background-color'], type: Color},
			{names: ['font', 'f'], type: Font, mixed: true}
		]
	},

	methods: {
		css: function() {
			var declarations = [];
			if (this.color != null) declarations.push("color:" + this.color.css());
			if (this.background != null) declarations.push("background:" + this.background.css());
			if (this.font != null) declarations = declarations.concat(this.font.css());
			return declarations;
		}
	}
});



exports.Style = Style;
