var oop = require('oop');

var Color = require('./color').Color;
var Font = require('./font').Font;



var Style = oop.Class({
	name: 'Style',

	schema: {
		inputToSpec: {
			'String': 'color',
			'Number': 'color'
		},
		properties: [
			{names: ['color', 'c'], type: Color, mixed: true},
			{names: ['background', 'bg', 'backgroundColor'], type: Color},
			{names: ['font', 'f'], type: Font, mixed: true}
		]
	}
});



exports.Style = Style;
