var oop = require('oop');



var OutlineNode = oop.Class({
	name: 'Outline Node',

	schema: {
		inputToSpec: {
			'String': 'label',
			'Array': 'children'
		},
		properties: [
			{names: ['label', 'title', 'string', 'str', 'display', 'node', 'text', 'txt'], type: oop.types.String, default: ""},
			{names: ['children', 'list', 'lst'], type: oop.types.Array, default: []}
		]
	},

	init: function() {
		for (var i = 0, length = this.children.length; i < length; i++) {
			this.children[i] = OutlineNode.factory(this.children[i]);
		}
	},

	methods: {
		add: function(node) {
			this.children.push(OutlineNode.factory(node));
		}
	}
});



exports.OutlineNode = OutlineNode;