var oop = require('std/oop');



var Parser = oop.Class({
	name: 'Parser',

	init: function(parser) {
		if (parser == null || parser.parse == null) {
			throw {
				msg: 'Invalid given parser',
				parser: parser
			};
		}

		this.parser = parser;
	},

	methods: {
		parse: function() {
			return this.postProcess(this.parser.parse.apply(this.parser, arguments));
		},

		/**
		 * To be overriden.
		 */
		postProcess: function(graph) {
			return graph;
		}
	}
});


exports.Parser = Parser;
