/*
 * TODO Check if the post-process in used already.
 * If not, remove it from here.
 * If yes, try to find an alternative.
 * Otherwise, stick with it :(
 */

var Class = require('oop').Class;



var Parser = Class({
	name: 'Parser',

	constructor: function(parser) {
		if (parser == null || parser.parse == null) {
			throw {
				msg: 'Invalid given parser',
				parser: parser
			};
		}

		this.parser = parser;
	},

	prototype: {
		parse: function() {
			return this.postProcess(this.parser.parse.apply(this, arguments));
		},

		/**
		 * To be overriden.
		 */
		postProcess: function(graph) {
			return graph;
		}
	}
});


exports.Parser = Parser;
